  return (
    <div className='entry-page bg-info'>
        
        <div className='jumbotron form-box'>
          {  (formLoad==='login')&&
        <Login
      
        ></Login>}
        {(formLoad==="reset")&&
         <PasswordReset
         handleOnChange={handleOnChange}
         handleOnResetSubmit={handleOnResetSubmit}
         formSwitcher={formSwitcher}
         email={email}  
        ></PasswordReset>}
        </div>

    
    </div>
  )
}
